package bca;

public class While {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         int x=1,sum=0;
         while(x<=10)
         {
        	 sum=sum+x;
        	 x++;
         }
        	 System.out.println("Summation of 1 to 10:-"+sum);
        	 
         }
        	 
	}


